using System.Collections;using System.Collections.Generic;using UnityEngine.UI;namespace DataUI.ListItems {    public class ExistingActivateGrammarResult : ExistingResult {        public void InitialiseMe(string resultID, string ruleID, string summaryTxt, string detailTxt) {            SetMyID(resultID);            transform.FindChild("GrammarID").GetComponent<Text>().text = ruleID;            transform.FindChild("GrammarSummary").GetComponent<Text>().text = summaryTxt;            transform.FindChild("GrammarDescription").GetComponent<Text>().text = detailTxt;        }    }}